import 'package:flutter/material.dart';import 'package:world_clock_app/extensions/string_extensions.dart';import 'package:world_clock_app/routing/route_names.dart';import 'package:world_clock_app/views/addcity/add_city_view.dart';import 'package:world_clock_app/views/home/home_view.dart';import 'package:world_clock_app/views/settings/settings_view.dart';Route<dynamic> generateRoute(RouteSettings settings) {  var routingData = settings.name.getRoutingData;  switch (routingData.route) {    case HomeRoute:      return _getPageRoute(HomeView(), settings);    case AddCity:      return _getPageRoute(AddCityView(), settings);    case Settings:      return _getPageRoute(SettingsView(), settings);    default:      return _getPageRoute(HomeView(), settings);  }}PageRoute _getPageRoute(Widget child, RouteSettings settings) {  return _FadeRoute(child: child, routeName: settings.name);}class _FadeRoute extends PageRouteBuilder {  final Widget child;  final String routeName;  _FadeRoute({this.child, this.routeName})      : super(          settings: RouteSettings(name: routeName),          pageBuilder: (            BuildContext context,            Animation<double> animation,            Animation<double> secondaryAnimation,          ) =>              child,          transitionsBuilder: (            BuildContext context,            Animation<double> animation,            Animation<double> secondaryAnimation,            Widget child,          ) =>              FadeTransition(            opacity: animation,            child: child,          ),        );}