import 'package:flutter/material.dart';BoxDecoration get lightClockDecoration {  return BoxDecoration(    boxShadow: <BoxShadow>[      BoxShadow(        color: Color.fromARGB(41, 255, 255, 255),        blurRadius: 54,        offset: Offset(-38, 0),      ),      BoxShadow(        color: Color.fromARGB(100, 199, 214, 234),        blurRadius: 54,        offset: Offset(50, 0),      ),      BoxShadow(        color: Color.fromARGB(100, 255, 255, 255),        blurRadius: 44,        offset: Offset(-11, 0),      ),      BoxShadow(        color: Color.fromARGB(100, 196, 212, 231),        blurRadius: 44,        offset: Offset(10, 0),      )    ],    color: Color(0xFFE7EEFB),    shape: BoxShape.circle,  );}BoxDecoration get darkClockDecoration {  return BoxDecoration(      boxShadow: <BoxShadow>[        BoxShadow(          color: Color.fromARGB(100, 16, 28, 59),          blurRadius: 54,          offset: Offset(-38, 0),        ),        BoxShadow(          color: Color(0xFF1A3781),          blurRadius: 54,          offset: Offset(50, 0),        ),        BoxShadow(          color: Color.fromARGB(100, 16, 28, 59),          blurRadius: 44,          offset: Offset(-11, 0),        ),      ],      color: Color(0xFF142550),      shape: BoxShape.circle);}