import 'package:flutter/material.dart';import 'package:world_clock_app/locator.dart';import 'package:world_clock_app/services/navigation_service.dart';class BottomViewModel extends ChangeNotifier {  int _currentIndex = 0;  get currentIndex => _currentIndex;  set currentIndex(int index) {    _currentIndex = index;    notifyListeners();  }  void navigateTo(String route){    locator<NavigationService>().navigateTo(route);  }}