import 'package:flutter/material.dart';import 'package:world_clock_app/styles/box_decoration_style.dart';import 'package:world_clock_app/widgets/clock/analog_clock.dart';class ClockView extends StatelessWidget {  @override  Widget build(BuildContext context) {    print(DateTime.now());    return Column(      mainAxisSize: MainAxisSize.min,      mainAxisAlignment: MainAxisAlignment.center,      crossAxisAlignment: CrossAxisAlignment.stretch,      children: <Widget>[        Expanded(          flex: 2,          child: Container(            padding: const EdgeInsets.symmetric(horizontal: 36.0),            child: AnalogClock(              decoration: Theme.of(context).brightness == Brightness.light                  ? lightClockDecoration                  : darkClockDecoration,              isLive: true,              hourHandColor: Theme.of(context).textTheme.display1.color,              minuteHandColor: Theme.of(context).textTheme.display2.color,              secondHandColor: Theme.of(context).textTheme.display3.color,              innerBackgroundColor:                  Theme.of(context).brightness == Brightness.light                      ? Color(0xFFEDF1FB)                      : Color(0xFF22335D),              showSecondHand: true,              numberColor: Theme.of(context).textTheme.display1.color,              showNumbers: true,              textScaleFactor: 1.4,              handStrokeWidth: 3,              showTicks: true,              tickColor: Colors.white,              showDigitalClock: true,              digitalClockColor: Theme.of(context).textTheme.display1.color,            ),          ),        ),        SizedBox(          height: 16.0,        ),        Expanded(          flex: 1,          child: Column(            children: <Widget>[              Text(                "Mumbai",                textAlign: TextAlign.center,                style: Theme.of(context).textTheme.subhead,              ),              SizedBox(                height: 8.0,              ),              Text(                "10:30 AM",                textAlign: TextAlign.center,                style: Theme.of(context).textTheme.title,              ),            ],          ),        )      ],    );  }}