import 'package:flutter/material.dart';import 'package:world_clock_app/constants/app_colors.dart';class AppBarWidget extends StatelessWidget implements PreferredSizeWidget {  AppBarWidget({Key key, this.topPadding, this.onAddPress, this.onEditPress})      : assert(onAddPress != null),        assert(onEditPress != null),        super(key: key);  final double topPadding;  final VoidCallback onAddPress;  final VoidCallback onEditPress;  @override  Widget build(BuildContext context) {    return Container(      padding: EdgeInsets.only(left: 16.0, right: 16.0, top: topPadding + 16.0),      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        crossAxisAlignment: CrossAxisAlignment.center,        children: <Widget>[          FloatingActionButton(            heroTag: "add_btn",            onPressed: onAddPress,            backgroundColor: cityTextColor,            elevation: 6.0,            child: Icon(              Icons.add,              color: Colors.white,            ),          ),          FloatingActionButton(            heroTag: "edit_btn",            onPressed: onEditPress,            backgroundColor: Colors.white,            elevation: 6.0,            child: Icon(              Icons.edit,              color: cityTextColor,            ),          ),        ],      ),    );  }  @override  Size get preferredSize => Size.fromHeight(kToolbarHeight + topPadding);}