import 'package:flutter/material.dart';import 'package:world_clock_app/constants/app_colors.dart';import 'package:world_clock_app/datamodels/clock_item_model.dart';import 'package:world_clock_app/widgets/clock/analog_clock.dart';class ClockItem extends StatelessWidget {  ClockItem({Key key, this.model})      : assert(model != null),        super(key: key);  final ClockItemModel model;  @override  Widget build(BuildContext context) {    return Container(      padding: const EdgeInsets.symmetric(        horizontal: 16.0,        vertical: 30.0,      ),      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        crossAxisAlignment: CrossAxisAlignment.center,        children: <Widget>[          Row(            mainAxisAlignment: MainAxisAlignment.center,            crossAxisAlignment: CrossAxisAlignment.center,            children: <Widget>[              AnalogClock(                decoration: BoxDecoration(                    color: Theme.of(context).brightness == Brightness.light                        ? clockListBackgroundLightColor                        : clockListBackgroundDarkColor,                    shape: BoxShape.circle),                showTicks: false,                hourHandColor: Theme.of(context).brightness == Brightness.light                    ? Colors.white                    : Colors.black,                minuteHandColor: Theme.of(context).brightness == Brightness.light                    ? clockListBackgroundDarkColor                    : clockListBackgroundLightColor,                showDigitalClock: false,                showSecondHand: true,                isLive: true,                showNumbers: false,                height: 65.0,                handStrokeWidth: 10,                width: 65.0,              ),              SizedBox(                width: 16.0,              ),              Column(                mainAxisSize: MainAxisSize.max,                crossAxisAlignment: CrossAxisAlignment.center,                mainAxisAlignment: MainAxisAlignment.center,                children: <Widget>[                  Text(                    "Paris",                    style: Theme.of(context).primaryTextTheme.title,                  ),                  SizedBox(                    height: 16.0,                  ),                  Text(                    "- 09:30",                    style: Theme.of(context).primaryTextTheme.subtitle,                  )                ],              )            ],          ),          Text(            "Today",            style: Theme.of(context).primaryTextTheme.body1,          )        ],      ),    );  }}