import 'package:flutter/material.dart';import 'package:world_clock_app/datamodels/clock_item_model.dart';import 'package:world_clock_app/widgets/clocks_list/clock_item.dart';class ClockList extends StatelessWidget {  @override  Widget build(BuildContext context) {    return ListView.separated(      shrinkWrap: false,      itemBuilder: (context, index) => ClockItem(        model: ClockItemModel(),      ),      separatorBuilder: (context, index) => Divider(        color: Color(0xFFD8D8D8),        thickness: 0.5,        height: 1.0,        indent: 20.0,        endIndent: 20.0,      ),      itemCount: 3,    );  }}